definition.script:
pipeline {
    agent { label params.NODE }

    stages {
        stage('Connect') {
            steps {
                sh """
                adb connect ${env.IPADDRESS}:${env.PORT}
                """
            }
        }
        stage('Monitor Battery') {
            steps {
                sh '''
                adb shell "echo temp > /sys/power/wake_lock"
                adb shell """
                    cd /sdcard
                    battery_stats.sh battery_monitor.txt &
                    pid=\\$!
                    sleep ${TIME}
                    kill -9 \\$pid
                """
                adb shell "echo temp > /sys/power/wake_unlock"

                rm -f sum.txt
                adb pull /sdcard/battery_monitor.txt .
                adb shell "rm /sdcard/battery_monitor.txt"
                gnuplot -e "set terminal png size 800,600; set output \\"battery_monitor.png\\"; plot \\"battery_monitor.txt\\""
                sum=0
                count=0
                for line in $(cat battery_monitor.txt)
                do
                    sum=$(expr $sum + $line)
                    count=$(expr $count + 1)
                done
                avg=$(expr $sum / $count)
                echo "#$i sum: $sum avg: $avg" >> sum.txt
                '''
            }
        }
        stage('Archive') {
            steps {
                archiveArtifacts artifacts: 'battery_monitor.txt', followSymlinks: false
                archiveArtifacts artifacts: 'battery_monitor.png', followSymlinks: false
                archiveArtifacts artifacts: 'sum.txt', followSymlinks: false
            }
        }
    }
}
