definition.script:
pipeline {
    agent { label params.NODE }

    stages {
        stage('Connect') {
            steps {
                sh """
                adb connect ${env.IPADDRESS}:${env.PORT}
                """
            }
        }
        stage('Hackbench') {
            steps {
                sh '''
                adb shell """
                cd /sdcard
                for i in \\$(seq 10)
                do
                    battery_stats.sh hackbench_battery_\\$i.txt &
                    pid=\\$!
                    hackbench -l 10000
                    kill -9 \\$pid
                    sleep 5
                done
                """

                for i in $(seq 10)
                do
                    adb pull /sdcard/hackbench_battery_$i.txt .
                    adb shell "rm /sdcard/hackbench_battery_$i.txt"
                    gnuplot -e "set terminal png size 800,600; set output \\"hackbench_battery_$i.png\\"; plot \\"hackbench_battery_$i.txt\\""
                done
                '''
            }
        }
        stage('Archive') {
            steps {
                archiveArtifacts artifacts: 'hackbench_battery_*.txt', followSymlinks: false
                archiveArtifacts artifacts: 'hackbench_battery_*.png', followSymlinks: false

            }
        }
    }
}
