definition.script:
pipeline {
    agent { label params.NODE }

    stages {
        stage('Connect') {
            steps {
                sh """
                adb connect ${env.IPADDRESS}:${env.PORT}
                """
                cleanWs()
            }
        }
        stage('Hackbench') {
            steps {
                sh '''
                adb shell "echo temp > /sys/power/wake_lock"
                adb shell """
                cd /sdcard
                for i in \\$(seq ${ITERATIONS})
                do
                    battery_stats.sh hackbench_battery_\\$i.csv &
                    pid=\\$!
                    hackbench -l ${LOOPS}
                    kill -9 \\$pid
                    sleep ${DELAY}
                done
                """
                adb shell "echo temp > /sys/power/wake_unlock"

                for i in $(seq ${ITERATIONS})
                do

                adb pull /sdcard/hackbench_battery_$i.csv .
                adb shell "rm /sdcard/hackbench_battery_$i.csv"
                python -c """
import pandas as pd
import matplotlib.pyplot as plt

data = pd.read_csv(\\"hackbench_battery_$i.csv\\")
df = pd.DataFrame(data)

df = df/1000
print(df.current.describe(percentiles=[0.9,0.95,0.99]))

ax = df.current.plot()

ax.set_title('Idle Current on Pixel 4')
ax.set_ylabel('Current mA')
ax.set_xlabel('Count')

plt.savefig(\\"hackbench_battery_$i.png\\")
                """ >> summary.txt
                done
                '''
            }
        }
        stage('Archive') {
            steps {
                archiveArtifacts artifacts: 'hackbench_battery_*.csv', followSymlinks: false
                archiveArtifacts artifacts: 'hackbench_battery_*.png', followSymlinks: false
                archiveArtifacts artifacts: 'summary.txt', followSymlinks: false
            }
        }
    }
}
